package utilities;


public class idLesson {
private int id ;
	
	



	public idLesson() {
	super();
	// TODO Auto-generated constructor stub
}

	@Override
	public String toString() {
		return "" + id + "";
	}

	public idLesson(int id) {
		super();
		this.id = id;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}
}
